    30.3 6.1 0;
    35.8 4.7 0;
    40.2 6.6 0];
speed = [30;30;30;30];
trajectory(car1, waypoints, speed);

pedestrian = actor(scenario, ...
    'ClassID', 4, ...
    'Length', 0.24, ...
    'Width', 0.45, ...
    'Height', 1.7, ...
    'Position', [25.9 0.5 0], ...
    'RCSPattern', [-8 -8;-8 -8], ...
    'Mesh', driving.scenario.pedestrianMesh, ...
    'Name', 'Pedestrian');
waypoints = [25.9 0.5 0;
    32.2 0.6 0];
speed = [5;5];
trajectory(pedestrian, waypoints, speed);

